<?php

namespace App\Repository;

/**
 * TextoDefinitivoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TextoDefinitivoRepository extends \Doctrine\ORM\EntityRepository
{
	public function getQbAll( $filter = null ) {
		$qb = $this->createQueryBuilder( 'td' );
		if ( $filter ) {
			if ( isset( $filter['numero'] ) ) {
				$numero = $filter['numero'];
				$qb
					->where( "td.numero = :numero" );
				$qb->setParameter( 'numero', $numero );
			}

			if ( isset( $filter['rama'] ) ) {
				$q = $filter['rama'];
				$qb
					->where( "td.rama = :rama" );
				$qb->setParameter( 'rama', $q );
			}
			
			if ( isset( $filter['tipoDocumento'] ) ) {
				$q = $filter['tipoDocumento'];
				$qb
					->where( "td.tipoDocumento = :tipoDocumento" );
				$qb->setParameter( 'tipoDocumento', $q );
			}

			if ( isset( $filter['aprobadoEnSesion'] ) ) {
				$q = $filter['aprobadoEnSesion'];
				$qb
					->andWhere( 'td.aprobadoEnSesion = :aprobadoEnSesion' )
					->setParameter( 'aprobadoEnSesion', $q );
			}

			if ( isset( $filter['expediente'] ) ) {
				$q = $filter['expediente'];
				$qb
					->innerJoin( 'td.dictamen', 'dictamen' )
					->innerJoin('dictamen.expediente', 'expediente')
					->andWhere( 'UPPER(expediente.expediente) LIKE UPPER(:expediente)' )
					->setParameter( 'expediente', "%$q%" );
			}
		}

		return $qb;
	}
}
